,p_item_display_point=>'BELOW'
,p_source_type=>'NATIVE_SQL_REPORT'
,p_query_type=>'SQL'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with total_applications as (',
'    select sum(case when f.created_on between to_date(:P1030_START_TIME_INTERVAL_1X, ''&DATE_FORMAT.'') and to_date(:P1030_END_TIME_INTERVAL_1X, ''&DATE_FORMAT.'')',
'                    then 1',
'                    else 0',
'               end) as apps_total_1x,',
'           sum(case when f.created_on between to_date(:P1030_START_TIME_INTERVAL_2X , ''&DATE_FORMAT.'') and to_date( :P1030_END_TIME_INTERVAL_2X, ''&DATE_FORMAT.'')',
'                    then 1',
'                    else 0',
'               end) as apps_total_2x',
'      from wwv_flows           f,',
'           wwv_flow_companies ws',
'     where ws.provisioning_company_id = f.security_group_id',
'       and ws.provisioning_company_id = :P1030_WORKSPACE_ID ),',
'applications_stats_1x as (',
'    select sum(wm.created_apps_cnt) as created_apps_total,',
'           sum(wm.removed_apps_cnt) as removed_apps_total',
'      from wwv_workspace_aggr_metrics wm',
'     where wm.workspace_id = :P1030_WORKSPACE_ID',
'       and wm.log_day between to_date(:P1030_START_TIME_INTERVAL_1X, ''&DATE_FORMAT.'') and to_date(:P1030_END_TIME_INTERVAL_1X, ''&DATE_FORMAT.'') ),',
'applications_stats_2x as (',
'    select sum(wm.created_apps_cnt) as created_apps_total,',
'           sum(wm.removed_apps_cnt) as removed_apps_total',
'      from wwv_workspace_aggr_metrics wm',
'     where wm.workspace_id = :P1030_WORKSPACE_ID',
'       and wm.log_day between to_date(:P1030_START_TIME_INTERVAL_2X , ''&DATE_FORMAT.'') and to_date( :P1030_END_TIME_INTERVAL_2X, ''&DATE_FORMAT.'') )',
'select stats_category,',
'       total,',
'       trim(to_char(delta,''999G999G999G999G999G999G990'')) as delta,',
'       to_char(round((delta / nullif(total,0)) * 100), ''999G999G999G999G999G999G990'') || case when total != 0 then ''%''',
'                                                                                         end as delta_pct,',
'       label_class,',
'       case when delta >  0 then ''up-arrow''',
'            when delta <  0 then ''down-arrow''',
'            else '' u-hidden''',
'       end as icon, ',
'       case when delta >  0 then ''u-success-text''',
'            when delta <  0 then ''u-danger-text''',
'            else '' u-hidden''',
'       end as class,',
'       case when delta >  0 then wwv_flow_lang_dev.builder_message(''UP'')',
'            when delta <  0 then wwv_flow_lang_dev.builder_message(''DOWN'')',
'       end as label',
' from (',
'    select 1                                                                     as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_CREATED_APPS'')        as stats_category,',
'           null                                                                  as label_class,',
'           nvl(sum(b.created_apps_total), 0)                                     as total,',
'           nvl(sum(b.created_apps_total), 0) - nvl(sum(d.created_apps_total), 0) as delta',
'      from applications_stats_1x b,',
'           applications_stats_2x d',
'     union all',
'     select 2                                                                    as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_REMOVED_APPS'')        as stats_category,',
'           null                                                                  as label_class,',
'           nvl(sum(b.removed_apps_total), 0)                                     as total,',
'           nvl(sum(b.removed_apps_total), 0) - nvl(sum(d.removed_apps_total), 0) as delta',
'      from applications_stats_1x b,',
'           applications_stats_2x d',
'     union all',
'     select 3                                                                      as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_TOTAL_APPLICATIONS'')    as stats_category,',
'           null                                                                    as label_class,',
'           nvl(sum(ta.apps_total_1x), 0)                                           as total,',
'           nvl(sum(ta.apps_total_1x), 0) - nvl(sum(ta.apps_total_2x), 0)           as delta',
'      from total_applications ta )',
'order by sort_order'))
,p_ajax_enabled=>'Y'
