'with time_frame as (',
'    select case when :P1035_SINCE < 100',
'                then trunc(sysdate) - nvl(:P1035_SINCE, :AI_DEFAULT_REPORTING_PERIOD)',
'                else to_date(:P1035_START_DATE)',
'           end start_interval_1x,',
'           case when :P1035_SINCE < 100',
'                then trunc(sysdate) - 1/24/3600',
'                else to_date(:P1035_END_DATE)',
'           end end_interval_1x',
'      from sys.dual ),',
'all_users as (',
'    select a.userid,',
'           sum(case',
'                   when a.time_stamp between tf.start_interval_1x and tf.end_interval_1x',
'                       then 1',
'                   else 0',
'               end)                               as page_events_total_1x,',
'           sum(case',
'                   when a.time_stamp between tf.start_interval_1x and tf.end_interval_1x',
'                       then decode(a.page_view_type, 2, 1, 0)',
'                   else 0',
'               end)                               as page_views_1x,',
'           count(distinct case',
'                   when a.time_stamp between tf.start_interval_1x and tf.end_interval_1x',
'                       then a.session_id',
'                   end)                           as distinct_sessions_total_1x,',
'           sum(case',
'                   when a.time_stamp between tf.start_interval_1x and tf.end_interval_1x',
'                       then decode(sqlerrm, null, 0, 1)',
'                   else 0',
'               end)                               as error_count_total_1x,',
'           count(distinct case',
'                              when a.time_stamp between tf.start_interval_1x and tf.end_interval_1x',
'                                  then a.flow_id',
'                              end)                as apps_count_1x,',
'           count(*)                               as page_events_total,',
'           sum(decode(a.page_view_type, 2, 1, 0)) as page_views,',
'           count(distinct a.session_id)           as distinct_sessions_total,',
'           sum(decode(sqlerrm, null, 0, 1))       as error_count_total,',
'           count(distinct a.flow_id)              as apps_count                                   ',
'      from wwv_flow_activity_log a,',
'           time_frame           tf',
'     where a.security_group_id not in (0, 10, 11, 12)',
'       and a.flow_id not between 4000 and 4999',
'       and a.time_stamp <= trunc(sysdate) - 1/24/3600',
'  group by a.userid ),',
'order_apps as (',
'     select row_number() over (order by page_events_total_1x desc,',
'                                        page_views_1x desc,',
'                                        distinct_sessions_total_1x desc,',
'                                        error_count_total_1x desc,',
'                                        apps_count_1x desc) as user_order,',
'            userid,                            ',
'            page_events_total_1x,',
'            trunc(nvl(page_events_total_1x, 0)/nullif(page_events_total, 0) * 100, 2) as pg_page_events,',
'            page_views_1x,',
'            trunc(nvl(page_views_1x, 0)/nullif(page_views, 0) * 100, 2) as pg_page_views,',
'            distinct_sessions_total_1x,',
'            trunc(nvl(distinct_sessions_total_1x, 0)/nullif(distinct_sessions_total, 0) * 100, 2) as pg_distinct_sessions_total,',
'            error_count_total_1x,',
'            trunc(nvl(error_count_total_1x, 0)/nullif(error_count_total, 0) * 100, 2) as pg_error_count_total,',
'            apps_count_1x,',
'            trunc(nvl(apps_count_1x, 0)/nullif(apps_count, 0) * 100, 2) as pg_apps_count',
'       from all_users )',
'select user_order user_rank, ',
'       userid,',
'       page_events_total_1x       as page_events_total,',
'       pg_page_events,',
'       page_views_1x              as page_views,',
'       pg_page_views,',
'       distinct_sessions_total_1x as distinct_sessions_total,',
'       pg_distinct_sessions_total,',
'       error_count_total_1x       as error_count_total,',
'       pg_error_count_total,',
'       apps_count_1x              as apps_count,',
'       pg_apps_count',
' from order_apps',
'where user_order <= :P1035_TOP_N_USERS'))
