'declare',
'  l_msg varchar2(4000);',
'  l_email varchar2(4000);',
'  l_schema_exists boolean := false;',
'  l_body_html varchar2(32767);',
'  l_workspace_id number;',
'begin',
'  for c1 in (select m.service_attribute_1',
'               from WWV_FLOW_PROVISION_SERICE_MOD m',
'              where id           = :P110_REQUEST_ID',
'                and service_name = ''NEW_SCHEMA'')',
'  loop',
'     l_schema_exists := wwv_flow_security.get_db_user_or_role(p_username => upper(c1.service_attribute_1)) is not null;',
'  end loop;  ',
' ',
'  wwv_flow_provisioning.add_schema_by_request(',
'    p_request_id   => :P110_REQUEST_ID,',
'    p_use_existing => l_schema_exists);',
'                 ',
'    for c1 in (select a.service_attribute_1, a.service_attribute_2 email_address, c.short_name, a.security_group_id',
'                   from wwv_flow_provision_serice_mod a,',
'                        wwv_flow_companies c',
'                  where a.security_group_id = c.provisioning_company_id',
'                    and a.id                = :P110_REQUEST_ID',
'                    and a.request_status    = ''APPROVED'') loop',
'        l_msg := chr(10)||',
'          wwv_flow_lang.system_message(p_name=>''F4050.PROV_SCHEMA_REQ_APPROVED'',',
'            p0=>c1.service_attribute_1,p1=>c1.short_name)||',
'          chr(10)||chr(10);',
'        l_email := c1.email_address;',
'        l_workspace_id := c1.security_group_id;',
'    end loop;',
'    ',
'    if l_workspace_id is null then',
'        raise program_error;',
'    end if;',
'        ',
'    l_msg := l_msg || :P110_Comments || chr(10) || chr(10);',
'    l_msg := l_msg || wwv_flow_mail.get_instance_url(p_security_group_id => l_workspace_id) || chr(10);',
'    l_body_html := wwv_flow_mail.convert_txt_to_html(p_txt_message => l_msg,p_security_group_id => l_workspace_id);',
