'    if l_email is null then',
'        for c1 in ( select ',
'                    b.email_address from wwv_flow_provision_serice_mod a, wwv_flow_fnd_user b               ',
'                    where a.security_group_id = b.security_group_id',
'                    and a.requested_by = b.user_name',
'                    and a.id = :P110_REQUEST_ID) loop',
'            l_email := c1.email_address;',
'        end loop;',
'    end if;',
'    ',
'    if l_email is not null then',
'        wwv_flow_mail.send(',
'            p_to     => l_email,',
'            p_from   => nvl(wwv_flow_platform.get_preference(''SMTP_FROM''),l_email),',
'            p_subj   => wwv_flow_lang.system_message(''F4050.PROV_CHANGE_REQ_APPROVED''),',
'            p_body   => l_msg,',
'            p_body_html => l_body_html); ',
