'declare',
'   l_security_group_id number;',
'   l_password          varchar2(255);',
'   l_email             varchar2(512);',
'   function get_security_group_id( p_workspace_name in varchar2 ) return number',
'   is',
'       l_security_group_id number := null;',
'   begin',
'       for c1 in (select provisioning_company_id',
'                    from wwv_flow_companies',
'                   where short_name = trim(upper(p_workspace_name))) loop',
'           l_security_group_id := c1.provisioning_company_id;',
'           exit;',
'       end loop;',
'       --',
'       return l_security_group_id;',
'   end get_security_group_id;',
'   ',
'begin',
'',
'    l_password := :P117_PASSWORD;',
'    :P117_PASSWORD := null;',
'',
'for c1 in (',
'select upper(c001) username, ',
'       lower(c002) email, ',
'       upper(c003) workspace,',
'       upper(c013) schema,',
'       c006 tab,',
'       c007 quota,',
'       c009 consumer_group,',
'       c010 invite,',
'       c011 auto_purge,',
'       c012 comments,',
'       SEQ_ID',
'  from wwv_flow_collections',
' where collection_name = ''BULK_USER_CREATE''',
'order by 1) loop',
'',
'    wwv_flow_cloud_db_services.create_workspace(',
'        p_workspace_name         => c1.workspace,',
'        p_schema_name            => c1.schema,',
'        p_max_size_mb            => c1.quota,',
'        p_install_sample_app     => ''N'',',
'        p_install_sample_ws      => ''N'',',
'        p_install_emp_dept       => c1.tab,',
'        p_allow_to_be_purged_yn  => c1.auto_purge,',
'        p_rm_consumer_group      => c1.consumer_group,',
'        p_workspace_description  => c1.comments);',
'',
'    wwv_flow_cloud_db_services.set_workspace_status(',
'        p_workspace_name => c1.workspace,',
'        p_workspace_status => ''ASSIGNED'' );',
'',
'    l_security_group_id  := get_security_group_id( p_workspace_name => c1.workspace );',
'    --    l_password := wwv_flow_provision.get_random_password( 32 );',
'',
'',
'    if instr(c1.username,''@'') > 0 and instr(c1.username,''.'') > 0 then',
'        l_email := c1.username;',
'    else',
'        l_email := null;',
'    end if;',
'',
'    wwv_flow_admin_api.create_user(',
'            p_security_group_id            => l_security_group_id,',
'            p_user_name                    => c1.username,',
'            p_first_name                   => null,',
'            p_last_name                    => null,',
'            p_web_password                 => l_password,',
'            p_email_address                => l_email,',
'            p_is_admin                     => ''Y'',',
'            p_change_password_on_first_use => ''N'' );',
'',
'end loop;',
'commit;',
'end;'))
