'       -- ',
'       -- This generated database user could already exist in the case that a workspace was removed, and ',
'       -- then attempted to be recreated. Check if the user exists in dba_users and then call ',
'       -- generate_schema_for_workspace again using this username to get a generated license plate style ',
'       -- schema name. (bug 34370679)',
'       --',
'       for c1 in (select null',
'                    from sys.dba_users',
'                   where username = l_schema_name) loop',
'            l_schema_name := substr(wwv_flow_platform.get_preference(''SELF_SERVICE_SCHEMA_PREFIX'')',
'               ||wwv_flow_provisioning.generate_schema_for_workspace(l_schema_name),1,128);',
'        end loop;',
'   end if;',
'',
'   if :P142_SECURITY_GROUP_ID is null then',
'       :P142_SECURITY_GROUP_ID := wwv_flow_id.next_val;',
