'    l_sql := ''create user ''||l_username_asserted||'' identified by ''||l_password_asserted;',
'',
'    begin',
'        if nvl(wwv_flow_platform.get_preference(''APEX_BUILDER_AUTHENTICATION''), ''APEX'') = ''DB'' then',
'            execute immediate l_sql;',
'        end if;',
