,p_component_template_options=>'#DEFAULT#:a-Report--inline:a-Report--staticRowColors:a-Report--horizontalBorders:a-Report--stretch'
,p_new_grid_row=>false
,p_item_display_point=>'BELOW'
,p_source_type=>'NATIVE_SQL_REPORT'
,p_query_type=>'SQL'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with jobs_stats as (           ',
'    select count(distinct case',
'                              when d.actual_start_date between to_timestamp(:P1000_START_INTERVAL, ''&DATE_TIME_FORMAT.'')',
'                                                       and trunc(sysdate) - 1/24/3600',
'                              then d.job_name',
'                          end) as jobs_cnt_1x,',
'           count(distinct d.job_name) as jobs_cnt              ',
'      from sys.dba_scheduler_job_run_details d',
'     where d.owner not in (''ORDS_METADATA'', ''SYS'', sys_context(''USERENV'',''CURRENT_USER''))',
'       and d.owner not in (select username',
'                             from sys.dba_users',
'                            where oracle_maintained = ''Y'')',
'       and d.actual_start_date between trunc(sysdate) - :AI_MAX_REPORTING_PERIOD and trunc(sysdate) - 1/24/3600',
'    union all',
'    select count(distinct case',
'                              when al.start_timestamp between to_timestamp(:P1000_START_INTERVAL, ''&DATE_TIME_FORMAT.'')',
'                                                      and trunc(sysdate) - 1/24/3600',
'                              then al.automation_id',
'                          end) as jobs_cnt_1x,    ',
'           count(distinct al.automation_id) as jobs_cnt',
'      from wwv_flow_automation_log al',
'     where al.start_timestamp <= trunc(sysdate) - 1/24/3600',
'    union all',
'    select count(distinct case',
'                              when sl.start_timestamp between to_timestamp(:P1000_START_INTERVAL, ''&DATE_TIME_FORMAT.'')',
'                                                      and trunc(sysdate) - 1/24/3600',
'                              then sl.web_src_module_id',
'                          end) as jobs_cnt_1x,    ',
'           count(distinct sl.web_src_module_id) as jobs_cnt',
'      from wwv_flow_websource_sync_log sl',
'     where sl.start_timestamp <= trunc(sysdate) - 1/24/3600 ),',
'aggr_jobs as (',
'    select sum(jobs_cnt_1x) as jobs_cnt_1x,',
'           sum(jobs_cnt) as jobs_cnt',
'      from jobs_stats)   ',
'select stats_category,',
'       total,',
'       trim(to_char(delta,''999G999G999G999G999G999G990'')) as delta,',
'       label_class,',
'       case when delta >  0 then ''up-arrow''',
'            when delta <  0 then ''down-arrow''',
'            else '' u-hidden''',
'       end as icon, ',
'       case when delta >  0 then ''u-success-text''',
'            when delta <  0 then ''u-danger-text''',
'            else '' u-hidden''',
'       end as class,',
'       case when delta >  0 then wwv_flow_lang_dev.builder_message(''UP'')',
'            when delta <  0 then wwv_flow_lang_dev.builder_message(''DOWN'')',
'       end as label       ',
' from (',
'    select 1                                                           as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_USER_JOBS'') as stats_category,',
'           null                                                        as label_class,',
'           nvl(jobs_cnt, 0)                                            as total,',
'           case when nvl(jobs_cnt_1x, 0) != 0',
'                then nvl(jobs_cnt, 0) - nvl(jobs_cnt_1x, 0)',
'                else 0',
'           end                                                         as delta',
'      from aggr_jobs )'))
