'      from wwv_flow_companies c,',
'           wwv_flow_company_schemas cs,',
'           wwv_schema_aggr_metrics sm',
'     where cs.security_group_id = c.provisioning_company_id',
'       and cs.schema = sm.schema',
'       and c.provisioning_company_id not in (0, 10, 11, 12)',
'       and log_day = (select max(log_day)',
'                        from wwv_schema_aggr_metrics w',
'                       where w.schema = sm.schema)',
'  group by c.provisioning_company_id,',
'           c.short_name )',
'    select workspace_id,',
'           workspace_name,',
'           existing_schemas,',
'           data_filesize_total / 1024 / 1024 / 1024 as data_filesize_total,',
'           free_space_total / 1024 / 1024 / 1024 as free_space_total,',
'           trunc((free_space_total / data_filesize_total) * 100, 2) as free_space_percentage',
