,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with time_frame as (',
'    select case',
'           when :P1020_SINCE < 100',
'           then trunc(sysdate) - nvl(:P1020_SINCE, :AI_DEFAULT_REPORTING_PERIOD)',
'           else to_date(:P1020_START_DATE)',
'           end start_interval_1x,',
'           case',
'           when :P1020_SINCE < 100',
'           then trunc(sysdate) - 1/24/3600',
'           else to_date(:P1020_END_DATE)',
'           end end_interval_1x',
'      from sys.dual ),',
'all_wksp as (',
'    select wm.workspace_id,',
'           wm.workspace_name,',
'           sum(case',
'                   when wm.log_day between tf.start_interval_1x and tf.end_interval_1x',
'                       then wm.page_events_cnt',
'                   else 0',
'               end)                      as page_events_cnt_1x,',
'           max(case',
'                   when wm.log_day between tf.start_interval_1x and tf.end_interval_1x',
'                       then wm.distinct_users_cnt',
'               end)                      as max_distinct_users_1x,',
'           sum(case',
'                   when wm.log_day between tf.start_interval_1x and tf.end_interval_1x',
'                       then wm.distinct_sessions_cnt',
'                   else 0',
'               end)                      as distinct_sessions_cnt_1x,',
'           sum(case',
'                   when wm.log_day between tf.start_interval_1x and tf.end_interval_1x',
'                       then wm.errors_cnt',
'                   else 0',
'               end)                      as errors_count_1x,           ',
'           max(case',
'                  when wm.log_day between tf.start_interval_1x and tf.end_interval_1x',
'                       then wm.workspace_size',
'                  end)                   as workspace_size_1x,',
'           sum(wm.page_events_cnt)       as page_events_cnt,',
'           max(wm.distinct_users_cnt)    as max_distinct_users,',
'           sum(wm.distinct_sessions_cnt) as distinct_sessions_cnt,',
'           sum(wm.errors_cnt)            as errors_count,           ',
'           max(wm.workspace_size)        as workspace_size',
'      from wwv_workspace_aggr_metrics wm,',
'           time_frame                 tf',
'     where wm.log_day <= trunc(sysdate) - 1',
'  group by workspace_id,',
'           workspace_name ), ',
'order_wksp as (',
'    select row_number() over( order by page_events_cnt_1x desc,',
'                                       max_distinct_users_1x desc,',
'                                       distinct_sessions_cnt_1x desc,',
'                                       errors_count_1x desc,',
'                                       workspace_size_1x desc ) as workspace_order,',
'           workspace_id,',
'           workspace_name,',
'           page_events_cnt_1x,',
'           trunc(nvl(page_events_cnt_1x, 0)/nullif(page_events_cnt, 0) * 100, 2)              as pg_page_events,',
'           max_distinct_users_1x,',
'           trunc(nvl(max_distinct_users_1x, 0)/nullif(max_distinct_users, 0) * 100, 2)        as pg_distinct_users,',
'           distinct_sessions_cnt_1x,',
'           trunc( nvl(distinct_sessions_cnt_1x, 0)/nullif(distinct_sessions_cnt, 0) * 100, 2) as pg_distinct_sessions,',
'           errors_count_1x,',
'           trunc(nvl(errors_count_1x, 0)/nullif(errors_count, 0) * 100, 2)                    as pg_errors_count,',
'           workspace_size_1x,',
'           trunc(nvl(workspace_size_1x, 0)/nullif(workspace_size, 0) * 100, 2)                as pg_workspace_size',
'      from all_wksp)',
'   select workspace_order           as workspace_rank,',
'           workspace_id,',
'           workspace_name,',
'           page_events_cnt_1x       as page_events_cnt,',
'           pg_page_events,',
'           max_distinct_users_1x    as max_distinct_users,',
'           pg_distinct_users,',
'           distinct_sessions_cnt_1x as distinct_sessions_cnt,',
'           pg_distinct_sessions,',
'           errors_count_1x          as errors_count,',
'           pg_errors_count,           ',
'           workspace_size_1x        as workspace_size,',
'           pg_workspace_size',
'      from order_wksp',
'     where workspace_order <= :P1020_TOP_N_WORKSPACES'))
