,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with jobs_stats_1x as (',
'    select sum(case when j.status in (''SUCCEEDED'', ''SUCCESS'') then 1 else 0 end ) as successful_jobs_total,',
'           sum(case when j.status in (''FAILED'', ''NO_ACTION'', ''INCOMPLETE'', ''FAILURE'', ''ABORTED'') then 1 else 0 end )    as failed_jobs_total',
'      from (',
'          select distinct jd.owner,',
'                 jd.job_name,',
'                 jd.status',
'            from sys.dba_scheduler_job_run_details jd',
'           where jd.owner not in (''ORDS_METADATA'', ''SYS'', sys_context(''USERENV'',''CURRENT_USER''))',
'             and jd.owner not in (select username',
'                                    from sys.dba_users',
'                                   where oracle_maintained = ''Y'')',
'             and jd.log_date between to_timestamp(:P1029_START_TIME_INTERVAL_1X, ''&DATE_TIME_FORMAT.'') and to_timestamp(:P1029_END_TIME_INTERVAL_1X, ''&DATE_TIME_FORMAT.'')',
'          union all ',
'          select distinct sys_context(''USERENV'',''CURRENT_USER''),',
'                 to_char(al.automation_id),',
'                 al.status',
'            from wwv_flow_automation_log al',
'          where al.start_timestamp between to_timestamp(:P1029_START_TIME_INTERVAL_1X, ''&DATE_TIME_FORMAT.'') and to_timestamp(:P1029_END_TIME_INTERVAL_1X, ''&DATE_TIME_FORMAT.'')',
'          union all',
'          select distinct sys_context(''USERENV'',''CURRENT_USER''),',
'                 to_char(sl.web_src_module_id),',
'                 sl.status',
'            from wwv_flow_websource_sync_log sl',
'           where sl.start_timestamp between to_timestamp(:P1029_START_TIME_INTERVAL_1X, ''&DATE_TIME_FORMAT.'') and to_timestamp(:P1029_END_TIME_INTERVAL_1X, ''&DATE_TIME_FORMAT.'')',
'             ) j ),',
'jobs_stats_2x as (',
'    select sum(case when j.status in (''SUCCEEDED'', ''SUCCESS'') then 1 else 0 end ) as successful_jobs_total,',
'           sum(case when j.status in (''FAILED'', ''NO_ACTION'', ''INCOMPLETE'', ''FAILURE'', ''ABORTED'') then 1 else 0 end )    as failed_jobs_total',
'      from (',
'          select distinct jd.owner,',
'                 jd.job_name,',
'                 jd.status',
'            from sys.dba_scheduler_job_run_details jd',
'           where jd.owner not in (''ORDS_METADATA'', ''SYS'', sys_context(''USERENV'',''CURRENT_USER''))',
'             and jd.owner not in (select username',
'                                    from sys.dba_users',
'                                   where oracle_maintained = ''Y'')',
'             and jd.log_date between to_timestamp(:P1029_START_TIME_INTERVAL_2X, ''&DATE_TIME_FORMAT.'') and to_timestamp(:P1029_END_TIME_INTERVAL_2X, ''&DATE_TIME_FORMAT.'')',
'          union all ',
'          select distinct sys_context(''USERENV'',''CURRENT_USER''),',
'                 to_char(al.automation_id),',
'                 al.status',
'            from wwv_flow_automation_log al',
'          where al.start_timestamp between to_timestamp(:P1029_START_TIME_INTERVAL_2X, ''&DATE_TIME_FORMAT.'') and to_timestamp(:P1029_END_TIME_INTERVAL_2X, ''&DATE_TIME_FORMAT.'')                                                                 ',
'          union all',
'          select distinct sys_context(''USERENV'',''CURRENT_USER''),',
'                 to_char(sl.web_src_module_id),',
'                 sl.status',
'            from wwv_flow_websource_sync_log sl',
'           where sl.start_timestamp between to_timestamp(:P1029_START_TIME_INTERVAL_2X, ''&DATE_TIME_FORMAT.'') and to_timestamp(:P1029_END_TIME_INTERVAL_2X, ''&DATE_TIME_FORMAT.'')                                                                 ',
'            ) j )',
'select stats_category,',
'       total,',
'       trim(to_char(delta,''999G999G999G999G999G999G990'')) as delta,',
'       to_char(round((delta / nullif(total,0)) * 100), ''999G999G999G999G999G999G990'') || case when total != 0 then ''%''',
'                                                                                         end as delta_pct,',
'       label_class,',
'       case when delta >  0 then ''up-arrow''',
'            when delta <  0 then ''down-arrow''',
'            else '' u-hidden''',
'       end as icon, ',
'       case when delta >  0 then ''u-success-text''',
'            when delta <  0 then ''u-danger-text''',
'            else '' u-hidden''',
'       end as class,',
'       case when delta >  0 then wwv_flow_lang_dev.builder_message(''UP'')',
'            when delta <  0 then wwv_flow_lang_dev.builder_message(''DOWN'')',
'       end as label',
' from (',
'    select 1                                                                 as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_SUCCESSJOBS'')     as stats_category,',
'           null                                                              as label_class,',
'           nvl(b.successful_jobs_total, 0)                                   as total,',
'           nvl(b.successful_jobs_total, 0) - nvl(d.successful_jobs_total, 0) as delta',
'      from jobs_stats_1x b,',
'           jobs_stats_2x d',
'    union all',
'    select 2                                                            as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_FAILEDJOBS'') as stats_category,',
'           null                                                         as label_class,',
'           nvl(b.failed_jobs_total, 0)                                  as total,',
'           nvl(b.failed_jobs_total, 0) - nvl(d.failed_jobs_total, 0)    as delta',
'      from jobs_stats_1x b,',
'           jobs_stats_2x d )',
'order by sort_order'))
