,p_display_sequence=>120
,p_region_css_classes=>'js-refresh-report a-Region--dashBG'
,p_region_template_options=>'#DEFAULT#:a-Region--noPadding'
,p_component_template_options=>'#DEFAULT#:a-Report--inline:a-Report--staticRowColors:a-Report--horizontalBorders:a-Report--stretch'
,p_item_display_point=>'BELOW'
,p_source_type=>'NATIVE_SQL_REPORT'
,p_query_type=>'SQL'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with messaging_stats as (',
'    select ',
'        sum(case when m.mail_message_created between to_date(:P1029_START_TIME_INTERVAL_1X, ''&DATE_FORMAT.'') and to_date(:P1029_END_TIME_INTERVAL_1X, ''&DATE_FORMAT.'')',
'                 then 1',
'                 else 0',
'            end )                                                                                                        as created,',
'        sum(case when m.mail_message_created between to_date(:P1029_START_TIME_INTERVAL_2X, ''&DATE_FORMAT.'') and to_date(:P1029_END_TIME_INTERVAL_2X, ''&DATE_FORMAT.'')',
'                 then 1',
'                 else 0',
'            end )                                                                                                        as created_interval,',
'        sum(case when m.mail_message_send_end between to_date(:P1029_START_TIME_INTERVAL_1X, ''&DATE_FORMAT.'') and to_date(:P1029_END_TIME_INTERVAL_1X, ''&DATE_FORMAT.'')',
'                 then 1',
'                 else 0',
'            end )                                                                                                        as send_end,',
'        sum(case when m.mail_message_send_end between to_date(:P1029_START_TIME_INTERVAL_2X, ''&DATE_FORMAT.'') and to_date(:P1029_END_TIME_INTERVAL_2X, ''&DATE_FORMAT.'')',
'                 then 1',
'                 else 0',
'            end )                                                                                                        as send_end_interval,',
'        sum(case when m.mail_send_error is not null',
'                  and m.mail_message_created between to_date(:P1029_START_TIME_INTERVAL_1X, ''&DATE_FORMAT.'') and to_date(:P1029_END_TIME_INTERVAL_1X, ''&DATE_FORMAT.'')',
'                 then 1',
'                else 0',
'            end )                                                                                                        as mail_errors,',
'        sum(case when m.mail_send_error is not null',
'                  and m.mail_message_created between to_date(:P1029_START_TIME_INTERVAL_2X, ''&DATE_FORMAT.'') and to_date(:P1029_END_TIME_INTERVAL_2X, ''&DATE_FORMAT.'')',
'                 then 1',
'                 else 0',
'            end)                                                                                                         as mail_errors_interval',
'    from wwv_flow_mail_log m ),',
'messaging_queue_stats as (',
'    select ',
'        sum(case when m.mail_message_created between to_date(:P1029_START_TIME_INTERVAL_1X, ''&DATE_FORMAT.'') and to_date(:P1029_END_TIME_INTERVAL_1X, ''&DATE_FORMAT.'')',
'                 then 1',
'                 else 0',
'            end)                                                                                                        as created,',
'        sum(case when m.mail_message_created between to_date(:P1029_START_TIME_INTERVAL_2X, ''&DATE_FORMAT.'') and to_date(:P1029_END_TIME_INTERVAL_2X, ''&DATE_FORMAT.'')',
'                 then 1',
'                 else 0',
'            end )                                                                                                       as created_interval',
'      from wwv_flow_mail_queue m )',
'select stats_category,',
'       total,',
'       trim(to_char(delta,''999G999G999G999G999G999G990'')) as delta,',
'       to_char(round((delta / nullif(total,0)) * 100), ''999G999G999G999G999G999G990'') || case when total != 0 then ''%''',
'                                                                                         end as delta_pct,',
'       label_class,',
'       case when delta >  0 then ''up-arrow''',
'            when delta <  0 then ''down-arrow''',
'            else '' u-hidden''',
'       end as icon, ',
'       case when delta >  0 then ''u-success-text''',
'            when delta <  0 then ''u-danger-text''',
'            else '' u-hidden''',
'       end as class,',
'       case when delta >  0 then wwv_flow_lang_dev.builder_message(''UP'')',
'            when delta <  0 then wwv_flow_lang_dev.builder_message(''DOWN'')',
'       end as label',
' from (',
'    select 1                                                               as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_EMAIL_CREATED'') as stats_category,',
'           null                                                            as label_class,',
'           nvl(created, 0)                                                 as total,',
'           nvl(created, 0) - nvl(created_interval, 0)                      as delta',
'      from messaging_stats',
'    union all     ',
'    select 2                                                              as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_EMAIL_QUEUED'') as stats_category,',
'           null                                                           as label_class,',
'           nvl(created, 0)                                                as total,',
'           null                                                           as delta',
'      from messaging_queue_stats',
'    union all ',
'    select 3                                                            as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_EMAIL_SENT'') as stats_category,',
'           null                                                         as label_class,',
'           nvl(send_end, 0)                                             as total,',
'           nvl(send_end, 0) - nvl(send_end_interval, 0)                 as delta',
'      from messaging_stats',
'    union all ',
'    select 4                                                       as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_ERROR'') as stats_category,',
'           ''u-danger-text''                                         as label_class,',
'           nvl(mail_errors, 0)                                     as total,',
'           nvl(mail_errors, 0) - nvl(mail_errors_interval, 0)      as delta',
'      from messaging_stats )',
