'with distinct_sessions_stats_1x as (',
'    select sum(distinct_sessions_cnt) as distinct_sessions_total',
'      from wwv_workspace_aggr_metrics wm',
'     where wm.log_day between to_date(:P1029_START_TIME_INTERVAL_1X, ''&DATE_FORMAT.'') and to_date(:P1029_END_TIME_INTERVAL_1X, ''&DATE_FORMAT.'')',
'    ),',
'distinct_sessions_stats_2x as (',
'    select sum(distinct_sessions_cnt) as distinct_sessions_total',
'      from wwv_workspace_aggr_metrics wm',
'     where wm.log_day between to_date(:P1029_START_TIME_INTERVAL_2X, ''&DATE_FORMAT.'') and to_date(:P1029_END_TIME_INTERVAL_2X, ''&DATE_FORMAT.'')    ',
'    )',
'select stats_category,',
'       total,',
'       trim(to_char(delta,''999G999G999G999G999G999G990'')) as delta,',
'       to_char(round((delta / nullif(total,0)) * 100), ''999G999G999G999G999G999G990'') || case when total != 0 then ''%''',
'                                                                                         end as delta_pct,',
'       label_class,',
'       case when delta >  0 then ''up-arrow''',
'            when delta <  0 then ''down-arrow''',
'            else '' u-hidden''',
'       end as icon, ',
'       case when delta >  0 then ''u-success-text''',
'            when delta <  0 then ''u-danger-text''',
'            else '' u-hidden''',
'       end as class,',
'       case when delta >  0 then wwv_flow_lang_dev.builder_message(''UP'')',
'            when delta <  0 then wwv_flow_lang_dev.builder_message(''DOWN'')',
'       end as label',
' from (',
'    select 1                                                                     as sort_order,',
'           wwv_flow_lang_dev.builder_message(''ADMIN_DIGEST_SESSIONS'')            as stats_category,',
'           null                                                                  as label_class,',
'           nvl(b.distinct_sessions_total, 0)                                     as total,',
'           nvl(b.distinct_sessions_total, 0) - nvl(d.distinct_sessions_total, 0) as delta',
'      from distinct_sessions_stats_1x b,',
'           distinct_sessions_stats_2x d )',
'order by sort_order'))
